mapper.insert:

	SELECT notice_tbl_seq.nextval FROM DUAL

INSERT INTO notice_tbl(
n_number, n_writer, n_title, n_content , n_photo
)
VALUES(
#{n_number}, '관리자', #{n_title}, #{n_content} , #{n_photo}
)

mapper.select:
SELECT n_number, n_title, n_content, n_photo
FROM notice_tbl
WHERE n_number = #{n_number}

mapper.update:
UPDATE notice_tbl
SET n_title = #{n_title},
	n_content = #{n_content}
	, n_photo = #{n_photo}
WHERE n_number = #{n_number}
